      canonicalUrl: 'https://www.nu.nl/formule-1/5989387/technisch-probleem-verstappen-in-kwalificatie-gp-italie-leclerc-pakt-pole.html',
      status: 'finished',
      sourceName: 'NU',
      imageUrl: 'https://media.nu.nl/m/d89xx5ja0yw0_wd1280.jpg/technisch-probleem-verstappen-in-kwalificatie-gp-italie-leclerc-pakt-pole.jpg',
      readingTime: 129.60000000000002,
